mapper.select:
select
  id, role_name, role_desc, delete_state, create_time, update_time
from spring_cloud.auth_role


        and id = #{id}


        and role_name = #{roleName}


        and role_desc = #{roleDesc}


        and delete_state = #{deleteState}


        and create_time = #{createTime}


        and update_time = #{updateTime}
